def convert_dynamodb_json_to_readable(dynamodb_data):
    try:
        if isinstance(dynamodb_data, dict):
            # Handle a single dictionary
            readable_dict = {}
            for key, value in dynamodb_data.items():
                for data_type, data_value in value.items():
                    readable_dict[key] = data_value
            return readable_dict

        elif isinstance(dynamodb_data, list):
            # Handle a list of dictionaries
            readable_list = []
            for item in dynamodb_data:
                if not isinstance(item, dict):
                    raise ValueError("Input list must contain dictionaries only")
                readable_dict = {}
                for key, value in item.items():
                    for data_type, data_value in value.items():
                        readable_dict[key] = data_value
                readable_list.append(readable_dict)
            return readable_list
        else:
            raise ValueError("Input must be a dictionary or a list of dictionaries")
    except json.JSONDecodeError as e:
        print(f"Error decoding JSON: {e}")
        return None